mapper.select:

        SELECT
	     mer_id,
	 	 title,
   		 old_price,
   		 price,
   		 stock,
   		 sale_number,
   		 sale_money,
   		 discount,
   		 postage,
   		 status,

   		 mer_cate_id,
   		 create_date,
   		 update_date
	FROM mer_tb


	AND price=#{price}


	AND sale_number=#{saleNumber}


	AND sale_money=#{saleMoney}


	AND mer_cate_id=#{merCateId}


	AND DATE(create_date)=DATE(#{createDate})


	AND DATE(update_date)=DATE(#{updateDate})


	AND status=#{status}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM mer_tb 


	AND price=#{price}


	AND sale_number=#{saleNumber}


	AND sale_money=#{saleMoney}


	AND mer_cate_id=#{merCateId}


	AND DATE(create_date)=DATE(#{createDate})


	AND DATE(update_date)=DATE(#{updateDate})


	AND status=#{status}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(mer_id)
FROM mer_tb


AND price=#{price}


AND sale_number=#{saleNumber}


AND sale_money=#{saleMoney}


AND mer_cate_id=#{merCateId}


AND DATE(create_date)=DATE(#{createDate})


AND DATE(update_date)=DATE(#{updateDate})


AND status=#{status}
